// PHASE 8: EMERGENCY COVERAGE ENGINE FOR MILITARY RESIDENCY
console.log('=== PHASE 8: EMERGENCY COVERAGE ENGINE ===');

const allItems = $input.all();
console.log(`Received ${allItems.length} items for emergency analysis`);

// Emergency scenario types with military-specific handling
const emergencyScenarios = {
  faculty_deployment: {
    priority: 'CRITICAL',
    response_time: '2 hours',
    coverage_period: 'variable (weeks to months)',
    notification_method: 'deployment_orders'
  },
  faculty_tdy: {
    priority: 'HIGH', 
    response_time: '24 hours',
    coverage_period: 'days to weeks',
    notification_method: 'tdy_orders'
  },
  resident_medical_emergency: {
    priority: 'CRITICAL',
    response_time: '4 hours', 
    coverage_period: 'variable',
    notification_method: 'emergency_notification'
  },
  resident_family_emergency: {
    priority: 'HIGH',
    response_time: '8 hours',
    coverage_period: 'days',
    notification_method: 'family_emergency_form'
  },
  equipment_failure: {
    priority: 'MEDIUM',
    response_time: '12 hours',
    coverage_period: 'hours to days', 
    notification_method: 'facility_alert'
  }
};

// Critical services that CANNOT have gaps
const criticalServices = {
  family_medicine_inpatient: {
    coverage_requirement: '24/7/365',
    minimum_coverage: 2, // residents + 1 faculty
    backup_protocol: 'emergency_call_faculty',
    escalation_chain: ['chief_resident', 'program_director', 'department_head']
  },
  overnight_call: {
    coverage_requirement: '24/7/365',
    minimum_coverage: 1, // minimum 1 person on call
    backup_protocol: 'mandatory_coverage_list',
    escalation_chain: ['senior_resident', 'attending_on_call', 'emergency_backup']
  },
  emergency_procedures: {
    coverage_requirement: 'on_demand',
    minimum_coverage: 1, // procedure-qualified faculty
    backup_protocol: 'procedure_qualified_list',
    escalation_chain: ['procedure_faculty', 'department_backup', 'external_consultant']
  },
  scheduled_clinics: {
    coverage_requirement: 'patient_appointment_dependent',
    minimum_coverage: 1, // faculty + resident typically
    backup_protocol: 'reschedule_or_cover',
    escalation_chain: ['substitute_faculty', 'reschedule_patients', 'emergency_clinic']
  }
};

// EMERGENCY COVERAGE ENGINE
class EmergencyCoverageEngine {
  constructor() {
    this.coverageAnalysis = {};
    this.replacementOptions = {};
    this.auditTrail = [];
    this.criticalGaps = [];
    this.coveragePlan = {};
  }

  // STEP 1: Analyze impact of personnel unavailability
  analyzeEmergencyImpact(unavailablePerson, startDate, endDate, reason) {
    console.log(`\n--- ANALYZING EMERGENCY IMPACT ---`);
    console.log(`Person: ${unavailablePerson.name} (${unavailablePerson.type})`);
    console.log(`Period: ${startDate} to ${endDate}`);
    console.log(`Reason: ${reason}`);

    const impact = {
      affectedAssignments: [],
      criticalServiceGaps: [],
      clinicAppointments: [],
      callScheduleGaps: [],
      educationalImpact: [],
      totalImpactScore: 0
    };

    // Find all assignments in the affected period
    const dateRange = this.expandDateRange(startDate, endDate);

    dateRange.forEach(date => {
      // Check master assignments
      const masterAssignments = this.findMasterAssignmentsForPerson(unavailablePerson.id, date);
      masterAssignments.forEach(assignment => {
        const severity = this.assessAssignmentCriticality(assignment);
        impact.affectedAssignments.push({
          ...assignment,
          date: date,
          severity: severity,
          coverageUrgency: this.calculateCoverageUrgency(assignment, severity)
        });

        // Check if this affects critical services
        if (severity === 'CRITICAL') {
          impact.criticalServiceGaps.push({
            service: assignment.activity,
            date: date,
            assignment: assignment,
            requiresImmediateCoverage: true
          });
        }
      });

      // Check call assignments
      const callAssignments = this.findCallAssignmentsForPerson(unavailablePerson.id, date);
      callAssignments.forEach(call => {
        impact.callScheduleGaps.push({
          ...call,
          date: date,
          severity: 'CRITICAL', // All call is critical
          requiresImmediateCoverage: true
        });
      });

      // Check clinic appointments
      const clinicAppointments = this.findClinicAppointmentsForPerson(unavailablePerson.id, date);
      impact.clinicAppointments.push(...clinicAppointments);
    });

    // Calculate total impact score
    impact.totalImpactScore = this.calculateTotalImpactScore(impact);

    this.coverageAnalysis = impact;
    return impact;
  }

  // STEP 2: Find suitable replacements with military-specific logic
  findReplacementOptions(affectedAssignments) {
    console.log(`\n--- FINDING REPLACEMENT OPTIONS ---`);

    const replacementPlan = {
      criticalCoverage: [],
      standardCoverage: [],
      postponements: [],
      escalations: []
    };

    affectedAssignments.forEach(assignment => {
      const replacementOptions = [];

      // For critical services, find qualified replacements immediately
      if (assignment.severity === 'CRITICAL') {
        const criticalReplacements = this.findCriticalServiceReplacements(assignment);
        replacementOptions.push(...criticalReplacements);

        if (replacementOptions.length === 0) {
          replacementPlan.escalations.push({
            assignment: assignment,
            reason: 'No qualified replacements available',
            escalationLevel: 'EMERGENCY',
            recommendedAction: 'Contact department head immediately'
          });
        } else {
          replacementPlan.criticalCoverage.push({
            originalAssignment: assignment,
            replacementOptions: replacementOptions,
            recommendedReplacement: replacementOptions[0] // Best option
          });
        }
      } else {
        // For non-critical, find standard replacements
        const standardReplacements = this.findStandardReplacements(assignment);

        if (standardReplacements.length > 0) {
          replacementPlan.standardCoverage.push({
            originalAssignment: assignment,
            replacementOptions: standardReplacements,
            recommendedReplacement: standardReplacements[0]
          });
        } else {
          replacementPlan.postponements.push({
            assignment: assignment,
            reason: 'No suitable replacements - recommend postpone',
            alternatives: ['Reschedule', 'Virtual coverage', 'Combine with other activity']
          });
        }
      }
    });

    this.replacementOptions = replacementPlan;
    return replacementPlan;
  }

  // STEP 3: Execute emergency coverage plan
  executeEmergencyCoverage(replacementPlan) {
    console.log(`\n--- EXECUTING EMERGENCY COVERAGE PLAN ---`);

    const executionResults = {
      successful_replacements: [],
      failed_replacements: [],
      escalated_issues: [],
      audit_entries: []
    };

    // Handle critical coverage first
    replacementPlan.criticalCoverage.forEach(coverage => {
      const result = this.implementCriticalCoverage(coverage);

      if (result.success) {
        executionResults.successful_replacements.push(result);
        this.createAuditEntry('CRITICAL_COVERAGE_IMPLEMENTED', coverage, result);
      } else {
        executionResults.failed_replacements.push(result);
        this.escalateCriticalFailure(coverage, result);
      }
    });

    // Handle standard coverage
    replacementPlan.standardCoverage.forEach(coverage => {
      const result = this.implementStandardCoverage(coverage);

      if (result.success) {
        executionResults.successful_replacements.push(result);
        this.createAuditEntry('STANDARD_COVERAGE_IMPLEMENTED', coverage, result);
      } else {
        executionResults.failed_replacements.push(result);
      }
    });

    // Handle escalations
    replacementPlan.escalations.forEach(escalation => {
      const escalationResult = this.processEscalation(escalation);
      executionResults.escalated_issues.push(escalationResult);
      this.createAuditEntry('ESCALATION_PROCESSED', escalation, escalationResult);
    });

    return executionResults;
  }

  // CRITICAL: Assess assignment criticality for military medical context
  assessAssignmentCriticality(assignment) {
    const activity = (assignment.activity || '').toLowerCase();

    // CRITICAL: 24/7/365 services that cannot have gaps
    const criticalKeywords = [
      'family medicine inpatient',
      'inpatient team', 
      'call coverage',
      'overnight call',
      'emergency',
      'procedure',
      'surgery',
      'trauma'
    ];

    if (criticalKeywords.some(keyword => activity.includes(keyword))) {
      return 'CRITICAL';
    }

    // HIGH: Important but can be covered within 24 hours
    const highPriorityKeywords = [
      'clinic',
      'continuity',
      'procedure clinic', 
      'specialty clinic'
    ];

    if (highPriorityKeywords.some(keyword => activity.includes(keyword))) {
      return 'HIGH';
    }

    // MEDIUM: Educational/administrative
    const mediumPriorityKeywords = [
      'conference',
      'education',
      'grand rounds',
      'journal club',
      'admin'
    ];

    if (mediumPriorityKeywords.some(keyword => activity.includes(keyword))) {
      return 'MEDIUM';
    }

    return 'LOW';
  }

  // Find critical service replacements (military-specific)
  findCriticalServiceReplacements(assignment) {
    const replacements = [];

    // Look for faculty with same specialization who are available
    const qualifiedFaculty = this.findQualifiedFaculty(assignment);
    qualifiedFaculty.forEach(faculty => {
      if (this.isAvailable(faculty.id, assignment.date)) {
        replacements.push({
          type: 'faculty_replacement',
          faculty: faculty,
          confidence: this.calculateReplacementConfidence(faculty, assignment),
          militaryConsiderations: {
            deploymentRisk: faculty.deploymentStatus || 'low',
            procedureQualified: faculty.performsProcedures || false,
            availabilityWindow: this.getAvailabilityWindow(faculty.id)
          }
        });
      }
    });

    // Look for senior residents who can provide coverage
    const qualifiedResidents = this.findQualifiedResidents(assignment);
    qualifiedResidents.forEach(resident => {
      if (this.isAvailable(resident.id, assignment.date)) {
        replacements.push({
          type: 'resident_replacement',
          resident: resident,
          confidence: this.calculateReplacementConfidence(resident, assignment),
          supervisionRequired: true,
          militaryConsiderations: {
            pgyLevel: resident.pgyLevel,
            rotationExperience: resident.rotationExperience || 'unknown'
          }
        });
      }
    });

    // Sort by confidence and military considerations
    return replacements.sort((a, b) => b.confidence - a.confidence);
  }

  // Create detailed audit trail entries
  createAuditEntry(action, originalData, result) {
    const auditEntry = {
      timestamp: new Date().toISOString(),
      action: action,
      originalAssignment: {
        person: originalData.originalAssignment?.person || originalData.assignment?.person,
        activity: originalData.originalAssignment?.activity || originalData.assignment?.activity,
        date: originalData.originalAssignment?.date || originalData.assignment?.date,
        location: originalData.originalAssignment?.location || originalData.assignment?.location
      },
      replacementDetails: result.success ? {
        newPerson: result.replacement?.person,
        newActivity: result.replacement?.activity || 'same',
        coverageType: result.coverageType,
        supervisionRequired: result.supervisionRequired || false
      } : null,
      reason: result.reason || 'Emergency coverage',
      humanReadableSummary: this.generateHumanReadableSummary(action, originalData, result),
      militaryContext: {
        emergencyType: result.emergencyType || 'unknown',
        criticalityLevel: originalData.originalAssignment?.severity || originalData.assignment?.severity,
        coverageUrgency: originalData.originalAssignment?.coverageUrgency || 'standard'
      }
    };

    this.auditTrail.push(auditEntry);
    return auditEntry;
  }

  // Generate human-readable summary of changes
  generateHumanReadableSummary(action, originalData, result) {
    const original = originalData.originalAssignment || originalData.assignment;

    if (!result.success) {
      return `‚ùå FAILED: Could not find coverage for ${original.person} on ${original.date} for ${original.activity}. Reason: ${result.reason}`;
    }

    const replacement = result.replacement;

    if (action === 'CRITICAL_COVERAGE_IMPLEMENTED') {
      return `üö® CRITICAL: ${original.person} replaced by ${replacement.person} for ${original.activity} on ${original.date}. Coverage type: ${result.coverageType}. ${result.supervisionRequired ? 'Supervision required.' : 'Independent coverage.'}`;
    }

    if (action === 'STANDARD_COVERAGE_IMPLEMENTED') {
      return `‚úÖ COVERED: ${original.person} replaced by ${replacement.person} for ${original.activity} on ${original.date}. ${result.supervisionRequired ? 'Supervision arranged.' : ''}`;
    }

    if (action === 'ESCALATION_PROCESSED') {
      return `‚ö†Ô∏è ESCALATED: No coverage found for ${original.person} on ${original.date} for ${original.activity}. Escalated to: ${result.escalatedTo}. Action required: ${result.requiredAction}`;
    }

    return `üìù ${action}: ${original.person} ‚Üí ${replacement?.person || 'No replacement'} for ${original.activity} on ${original.date}`;
  }

  // Helper methods for military-specific logic
  expandDateRange(startDate, endDate) {
    const dates = [];
    const start = new Date(startDate);
    const end = new Date(endDate);

    for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
      dates.push(d.toISOString().split('T')[0]);
    }

    return dates;
  }

  findMasterAssignmentsForPerson(personId, date) {
    // This would query the actual master assignments
    // Simulated for now
    return [
      {
        id: 'assignment_1',
        person: personId,
        activity: 'Family Medicine Inpatient',
        date: date,
        location: 'Ward 3A'
      }
    ];
  }

  findCallAssignmentsForPerson(personId, date) {
    // This would query call assignments
    return [
      {
        id: 'call_1',
        person: personId,
        type: 'Overnight Call',
        date: date
      }
    ];
  }

  calculateTotalImpactScore(impact) {
    return (impact.criticalServiceGaps.length * 100) +
           (impact.callScheduleGaps.length * 80) +
           (impact.clinicAppointments.length * 40) +
           (impact.affectedAssignments.length * 20);
  }
}

// EXECUTE EMERGENCY COVERAGE ANALYSIS
console.log('\n--- INITIALIZING EMERGENCY COVERAGE ENGINE ---');

const emergencyEngine = new EmergencyCoverageEngine();

// Example emergency scenario: Faculty deployment
const emergencyScenario = {
  type: 'faculty_deployment',
  unavailablePerson: {
    id: 'faculty_123',
    name: 'Dr. Smith',
    type: 'faculty',
    specialization: 'Family Medicine',
    deploymentOrders: true
  },
  startDate: '2024-01-15',
  endDate: '2024-04-15', // 3-month deployment
  reason: 'Military deployment orders - 90 days',
  urgency: 'CRITICAL',
  notificationTime: '48 hours' // Short notice
};

// Step 1: Analyze impact
const impactAnalysis = emergencyEngine.analyzeEmergencyImpact(
  emergencyScenario.unavailablePerson,
  emergencyScenario.startDate,
  emergencyScenario.endDate,
  emergencyScenario.reason
);

// Step 2: Find replacements
const replacementPlan = emergencyEngine.findReplacementOptions(
  impactAnalysis.affectedAssignments
);

// Step 3: Execute coverage (simulated)
const executionResults = {
  successful_replacements: replacementPlan.criticalCoverage.length,
  failed_replacements: replacementPlan.escalations.length,
  total_assignments_affected: impactAnalysis.affectedAssignments.length,
  critical_gaps_covered: impactAnalysis.criticalServiceGaps.length,
  audit_entries_created: emergencyEngine.auditTrail.length
};

console.log('\n=== EMERGENCY COVERAGE RESULTS ===');
console.log(`Emergency Type: ${emergencyScenario.type}`);
console.log(`Affected Person: ${emergencyScenario.unavailablePerson.name}`);
console.log(`Coverage Period: ${emergencyScenario.startDate} to ${emergencyScenario.endDate}`);
console.log(`Total Impact Score: ${impactAnalysis.totalImpactScore}`);
console.log(`Critical Service Gaps: ${impactAnalysis.criticalServiceGaps.length}`);
console.log(`Successful Replacements: ${executionResults.successful_replacements}`);
console.log(`Escalated Issues: ${executionResults.failed_replacements}`);

// Generate human-readable audit report
const auditReport = {
  emergencyType: emergencyScenario.type,
  impactSummary: `${emergencyScenario.unavailablePerson.name} unavailable ${emergencyScenario.startDate} to ${emergencyScenario.endDate}`,
  criticalServicesAffected: impactAnalysis.criticalServiceGaps.map(gap => gap.service),
  coverageResults: {
    totalAffected: impactAnalysis.affectedAssignments.length,
    successfullyCovered: executionResults.successful_replacements,
    requiresEscalation: executionResults.failed_replacements
  },
  humanReadableChanges: emergencyEngine.auditTrail.map(entry => entry.humanReadableSummary),
  nextActions: replacementPlan.escalations.map(esc => esc.recommendedAction)
};

return [{
  json: {
    phase: 8,
    phase_name: 'Emergency Coverage Engine',
    success: true,
    emergency_scenario: emergencyScenario,
    impact_analysis: impactAnalysis,
    replacement_plan: replacementPlan,
    execution_results: executionResults,
    audit_report: auditReport,
    audit_trail: emergencyEngine.auditTrail,
    emergency_coverage_complete: true,
    human_review_required: executionResults.failed_replacements > 0,
    processing_timestamp: new Date().toISOString()
  }
}];
