  };
}

// SIMPLIFIED: Resident-focused template prioritization
function prioritizeTemplatesForResident(candidateTemplates) {
  return candidateTemplates.sort((a, b) => {
    const getPriority = (template) => {
      const activity = template['Activity'] || '';
      // Prioritize educational value for residents
      if (activity.toLowerCase().includes('procedure') || activity.toLowerCase().includes('vasectomy')) return 3;
      if (activity.toLowerCase().includes('clinic')) return 2;
      if (activity.toLowerCase().includes('emergency')) return 2;
      return 1;
    };
    return getPriority(b) - getPriority(a);
  });
}

// RESIDENT-FOCUSED PAIRING ALGORITHM
console.log('\n--- STARTING RESIDENT ROTATION PAIRING ---');

const smartPairings = [];
const unavailableSlots = [];
const substitutionApplied = [];
const blockStats = {};

halfDayRecords.forEach(halfDay => {
  const blockId = Array.isArray(halfDay.Block) ? halfDay.Block[0] : halfDay.Block;
  const hdWeek = Array.isArray(halfDay['Week of the Block']) 
    ? halfDay['Week of the Block'][0] 
    : halfDay['Week of the Block'];
  const date = halfDay['Date of Day of the Week of Block'];
  const timeOfDay = halfDay['Time of Day'];

  if (!blockStats[blockId]) blockStats[blockId] = { 
    matched: 0, 
    unavailable: 0, 
    substituted: 0 
  };

  // Find matching rotation templates by day, time, and week
  const candidateTemplates = rotationTemplates.filter(template =>
    halfDay['Day of the Week of Block'] === template['Day'] &&
    timeOfDay === (template['Halfâ€‘day'] || template['Half-day']) &&
    Number(hdWeek) === Number(template['Week of Rotation'])
  );

  if (candidateTemplates.length === 0) {
    unavailableSlots.push({
      halfDayId: halfDay['HDoWoB ID'],
      blockId: blockId,
      date: date,
      reason: 'No matching rotation templates found',
      searchCriteria: `Block ${blockId}: ${halfDay['Day of the Week of Block']} ${timeOfDay} Week ${hdWeek}`
    });
    blockStats[blockId].unavailable++;
    return;
  }

  // Prioritize templates for resident education value
  const prioritizedTemplates = prioritizeTemplatesForResident(candidateTemplates);
  const selectedTemplate = prioritizedTemplates[0];

  // Apply resident-focused substitution logic
  const finalTemplate = applyResidentAbsenceSubstitution(
    selectedTemplate, date, timeOfDay, residentAbsences, halfDay
  );

  // Create smart pairing for resident education
  const pairing = {
    HDoWoB_ID: halfDay['HDoWoB ID'],
    Rotation_Slot_ID: finalTemplate['Rotation Slot ID'],
    Block_ID: blockId,
    Date: date,
    Time_Of_Day: timeOfDay,
    Final_Activity: finalTemplate['Activity'],
    Original_Activity: selectedTemplate['Activity'],
    Absence_Substitution_Applied: finalTemplate['Absence Applied'],
    Educational_Priority: 'Resident rotation assignment',
    Verification: {
      halfDay: `Block ${blockId}: ${halfDay['Day of the Week of Block']} ${timeOfDay} Week ${hdWeek}`,
      template: `${finalTemplate['Rotation Slot']} (${finalTemplate['Activity']})`,
      matchType: 'resident_education_focused',
      candidateTemplateCount: candidateTemplates.length,
      pairing_phase: 'Phase 1 - Resident Rotations'
    }
  };

  smartPairings.push(pairing);
  blockStats[blockId].matched++;

  // Track substitutions
  if (finalTemplate['Absence Applied']) {
    substitutionApplied.push({
      halfDayId: halfDay['HDoWoB ID'],
      blockId: blockId,
      date: date,
      originalActivity: selectedTemplate['Activity'],
      replacementActivity: finalTemplate['Activity'],
      absenceType: finalTemplate['Absence Type'],
      substitutionReason: 'Resident absence accommodation'
    });
    blockStats[blockId].substituted++;
  }
});

